import React from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCarrot } from "@fortawesome/free-solid-svg-icons";
import { faTimesCircle } from "@fortawesome/free-regular-svg-icons";

const GlanceBox = ({tags}) =>{
    return (
        <div>
            <p>At A Glance</p>
            <Row>{tags.map(tag => <GlanceTag icon={tag}/>)}</Row>
        </div>
    );
};

export default GlanceBox;

const GlanceTag = ({icon}) =>{
    const Icon = icon = 'gluten' ? <><FontAwesomeIcon icon={faCarrot}></FontAwesomeIcon><p>Open</p></> : <><FontAwesomeIcon icon={faCarrot}></FontAwesomeIcon><p>Open</p></>

    return (
        <IconBox>
            {Icon}
        </IconBox>
    );
};

const IconBox = styled.div`
    width: auto;
    border-radius: 15px;
    border: 3px solid black;
    box-shadow: 2px 5px 20px rgba(0, 0, 0, 0.10);
`;

const Row = styled.div`
    display: flex;
    flex-direction: row;
`;
