import React from 'react';
import styled from 'styled-components';

export const MainBox = ({score, qrCode, materials, manufacturing, use, transport, endOfLife}) =>{
    return (
        <Container>
            <Column>
                <h1>{score}</h1>
                <div>kg CO2E</div>
                <div>{qrCode}</div>
            </Column>
            <hr></hr>
            <Column>
                <h2>Materials</h2>
                <h2>Manufacturing</h2>
                <h2>Use</h2>
                <h2>Transport</h2>
                <h2>End of Life</h2>
            </Column>
            <Column>
                <Row><h2>{materials}</h2><p>kg CO2e</p></Row>
                <Row><h2>{manufacturing}</h2><p>kg CO2e</p></Row>
                <Row><h2>{use}</h2><p>kg CO2e</p></Row>
                <Row><h2>{transport}</h2><p>kg CO2e</p></Row>
                <Row><h2>{endOfLife}</h2><p>kg CO2e</p></Row>
            </Column>
        </Container>
    );
};

export default MainBox;

const Container = styled.div`
    border-radius: 5px;
    border: 2px solid black;
    display: inline-flex;
    flex-direction: row;
    padding: 10px;

    h1{
        margin: 0;
        font-size: 4rem;
    }

    hr.vertical {
        height:100%; /* you might need some positioning for this to work, see other questions about 100% height */
        width:0;
        border:1px solid black;
    }
`;

const Column = styled.div`
    padding: 10px;

    div{
        padding: 2px;
    }
`;

const Row = styled.div`
    display: flex;
    flex-direction: row;

    p{
        margin-top: auto;
        margin-bottom: auto;
        padding-left: 2px;
        font-size: 0.2rem;
    }

    h2{
        margin-top: 0.6rem;
    }

`;
